import { doc } from "win-doc";

var getTextarea = function getTextarea() {
  return doc().createElement('textarea');
};

var htmlEncode = function htmlEncode(s) {
  var textarea = getTextarea();
  textarea.appendChild(doc().createTextNode(s));
  return textarea.innerHTML;
};

var htmlDecode = function htmlDecode(s) {
  var textarea = getTextarea();
  textarea.innerHTML = s;
  return textarea.textContent;
};

export { htmlEncode, htmlDecode };
