import callfunc from "./callfunc.mjs";

/**
 * only run lasttime.
 */
var debounce = function debounce(func, defaultDelay) {
  var timer;
  return function (option) {
    var {
      delay = 250,
      args,
      scope
    } = option || {};
    clearTimeout(timer);
    timer = setTimeout(function () {
      return callfunc(func, args, scope);
    }, defaultDelay !== null && defaultDelay !== void 0 ? defaultDelay : delay);
  };
};
export default debounce;
