import { expect } from 'chai';
import sanitizeHtml from 'sanitize-html';
import fixHtml from '../index';
describe('Test fixHtml', function () {
  it('simple test', function () {
    var a = fixHtml();
    expect(a).to.equal('<html xmlns="http://www.w3.org/1999/xhtml"><head></head><body></body></html>');
  });
  it('fix un-close tag', function () {
    var input = '<a a=" b="c">"=""</a><span a=" b="c">foo';
    var a = fixHtml(input, sanitizeHtml);
    expect(a).to.equal('<html xmlns="http://www.w3.org/1999/xhtml"><head></head><body><a>"=""</a><span>foo</span></body></html>');
  });
  it('test doctype', function () {
    var input = '<!doctype html><html></html>';
    var a = fixHtml(input, sanitizeHtml);
    var b = fixHtml(input);
    var expected = '<!DOCTYPE html><html xmlns="http://www.w3.org/1999/xhtml"><head></head><body></body></html>';
    expect(a).to.equal(expected);
    expect(b).to.equal(expected);
  });
  it('test special case', function () {
    var input = '<img alt=" height=" 60" src="https://fs.xxx.com.tw/upload/harddisc/file_name_541.png" width="120" />';
    var a = fixHtml(input, sanitizeHtml);
    expect(a).to.equal('<html xmlns="http://www.w3.org/1999/xhtml"><head></head><body><img alt=" height=" src="https://fs.xxx.com.tw/upload/harddisc/file_name_541.png" width="120" /></body></html>');
  });
  it('convert xml to html', function () {
    var input = '&amp;';
    var a = fixHtml(input, sanitizeHtml);
    var expected = '<html xmlns="http://www.w3.org/1999/xhtml"><head></head><body>&</body></html>';
    expect(a).to.equal(expected);
  });
});
describe('Test component only', function () {
  it('simple test', function () {
    var input = '<span />';
    var a = fixHtml(input, sanitizeHtml, true);
    expect(a).to.equal('<span></span>');
  });
  it('fix un-close tag', function () {
    var input = '<a a=" b="c">"=""</a><span a=" b="c">foo';
    var a = fixHtml(input, sanitizeHtml, true);
    expect(a).to.equal('<a>"=""</a><span>foo</span>');
  });
  it('convert xml to html', function () {
    var input = '&amp;';
    var a = fixHtml(input, sanitizeHtml, true);
    var expected = '&';
    expect(a).to.equal(expected);
  });
});